mapper.update:
update ktx_order set
 rdate=sysdate, stime=#{stime}, dtime=#{dtime}, price=#{price}, trainnum=#{trainnum},
 chairnum=#{chairnum}, depPlandTime=#{depPlandTime}, cardnum=#{cardnum}, ticketqty=#{ticketqty}
where reservnum=#{reservnum}

mapper.select:
SELECT id, sstation, depPlandTime, stime, dstation, ticketqty, price, reservnum, trainnum, chairnum, cancel, depPlaceId, arrPlaceId, cardnum, email, to_char(cdate,'yyyy-mm-dd') cdate, rdate, cid
FROM KTX_ORDER


        where email=#{email}


        where cardnum=#{cardnum}


        where id=#{id}

SELECT count(*) from ktx_order


        where email=#{email}


        where cardnum=#{cardnum}

mapper.update:
update ktx_order set
cancel=1, cdate=sysdate
where reservnum=#{reservnum}

mapper.select:
SELECT rno, sstation, rdate, depPlandTime, stime, dtime, dstation, price, reservnum, trainnum, chairnum, id, cardnum, cancel from (select rownum rno, sstation, to_char(rdate,'yyyy-mm-dd') rdate, depPlandTime, stime, dtime, dstation, price, reservnum, trainnum, chairnum, id, cardnum, cancel from (select sstation, rdate, depPlandTime, stime, dtime, dstation, price, reservnum, trainnum, chairnum, id, cardnum, cancel FROM KTX_ORDER order by rdate desc)
where reservnum LIKE '%'||#{cvalue}||'%'
where id LIKE '%'||#{cvalue}||'%'
) where rno between #{start} and #{end}
